numberVariable.codeExample:
let score = 100;
let lives = 3;
console.log('Score:', score);
console.log('Lives:', lives);

stringCombine.codeExample:
let first = 'Super';
let last = 'Mario';
let fullName = first + ' ' + last;
console.log(fullName); // Ta-da! Super Mario!

mathPreview.codeExample:
let score = 100;
let bonus = 50;
let total = score + bonus;
console.log('Total score:', total); // Boom! 150!

dataTypes.codeExample:
// Strings (text)
let player = 'Hero';

// Numbers (can be whole or decimal!)
let level = 5;
let health = 95.5;

// Booleans (true/false)
let isGameOver = false;

moreExamples.codeExample:
