class Enemy:
    def __init__(self, x, y, enemy_type=1):
        self.rect = pygame.Rect(x, y, 40, 30)
        self.type = enemy_type
        self.speed = enemy_type  # Harder enemies move faster
        self.direction = 1
        self.drop_distance = 40
        self.shoot_chance = 0.001 * enemy_type  # Higher types shoot more
        self.points = 10 * enemy_type

    def update(self):
        self.rect.x += self.speed * self.direction

    def drop_down(self):
        self.rect.y += self.drop_distance
        self.direction *= -1  # Reverse direction

    def should_shoot(self):
        return random.random() < self.shoot_chance

    def shoot(self):
        return EnemyBullet(self.rect.centerx, self.rect.bottom)

    def draw(self, screen):
        color = [RED, BLUE, (255, 0, 255)][self.type - 1]  # Different colors
        # Draw alien shape
        pygame.draw.ellipse(screen, color, self.rect)
        # Draw eyes
        eye_y = self.rect.y + 10
        pygame.draw.circle(screen, WHITE, (self.rect.x + 12, eye_y), 3)
        pygame.draw.circle(screen, WHITE, (self.rect.x + 28, eye_y), 3)

class PowerUp:
    def __init__(self, x, y, power_type):
        self.rect = pygame.Rect(x, y, 30, 30)
        self.type = power_type  # 'rapid', 'shield', 'life'
        self.speed = 2
        self.active = True

    def update(self):
        self.rect.y += self.speed
        if self.rect.top > SCREEN_HEIGHT:
            self.active = False

    def draw(self, screen):
        colors = {'rapid': YELLOW, 'shield': BLUE, 'life': GREEN}
        pygame.draw.circle(screen, colors.get(self.type, WHITE), self.rect.center, 15)

