class Game:
    def __init__(self):
        self.reset_game()
        self.state = 'MENU'  # MENU, PLAYING, PAUSED, GAME_OVER
        self.high_score = 0

    def reset_game(self):
        self.player = Player(SCREEN_WIDTH // 2 - 25, SCREEN_HEIGHT - 60)
        self.bullets = []
        self.enemy_bullets = []
        self.enemies = []
        self.power_ups = []
        self.score = 0
        self.level = 1
        self.spawn_enemies()

    def spawn_enemies(self):
        # Create grid of enemies
        rows = min(3 + self.level // 2, 6)  # More rows each level
        cols = 8
        enemy_type = min((self.level - 1) // 3 + 1, 3)  # Harder enemies

        for row in range(rows):
            for col in range(cols):
                x = 75 + col * 80
                y = 50 + row * 50
                self.enemies.append(Enemy(x, y, enemy_type))

    def handle_collisions(self):
        # Player bullets hit enemies
        for bullet in self.bullets[:]:
            for enemy in self.enemies[:]:
                if bullet.rect.colliderect(enemy.rect):
                    self.bullets.remove(bullet)
                    self.enemies.remove(enemy)
                    self.score += enemy.points

                    # Chance to drop power-up
                    if random.random() < 0.1:  # 10% chance
                        power_type = random.choice(['rapid', 'shield', 'life'])
                        self.power_ups.append(PowerUp(enemy.rect.centerx, enemy.rect.centery, power_type))
                    break

        # Enemy bullets hit player
        for bullet in self.enemy_bullets[:]:
            if bullet.rect.colliderect(self.player.rect):
                self.enemy_bullets.remove(bullet)
                if self.player.hit():  # Returns True if game over
                    self.state = 'GAME_OVER'
                    if self.score > self.high_score:
                        self.high_score = self.score

        # Player collects power-ups
        for power in self.power_ups[:]:
            if power.rect.colliderect(self.player.rect):
                self.power_ups.remove(power)
                if power.type == 'life':
                    self.player.lives += 1
                elif power.type == 'rapid':
                    self.player.shoot_cooldown = 5  # Faster shooting
                # Add more power-up effects

        # Enemies reach player
        for enemy in self.enemies:
            if enemy.rect.bottom >= self.player.rect.top:
                self.state = 'GAME_OVER'

    def next_level(self):
        self.level += 1
        self.spawn_enemies()
        # Bonus points for completing level
        self.score += 100 * self.level

