# COMPLETE SPACE DEFENDER GAME
import pygame
import random

# [Include all previous classes here]

def main():
    pygame.init()
    screen = pygame.display.set_mode((SCREEN_WIDTH, SCREEN_HEIGHT))
    pygame.display.set_caption('Space Defender')
    clock = pygame.time.Clock()

    game = Game()
    running = True

    while running:
        # Handle events
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                running = False

            if event.type == pygame.KEYDOWN:
                if game.state == 'MENU':
                    if event.key == pygame.K_SPACE:
                        game.state = 'PLAYING'
                        game.reset_game()

                elif game.state == 'PLAYING':
                    if event.key == pygame.K_SPACE:
                        bullet = game.player.shoot()
                        if bullet:
                            game.bullets.append(bullet)
                    elif event.key == pygame.K_p:
                        game.state = 'PAUSED'

                elif game.state == 'PAUSED':
                    if event.key == pygame.K_p:
                        game.state = 'PLAYING'

                elif game.state == 'GAME_OVER':
                    if event.key == pygame.K_r:
                        game.state = 'MENU'

        # Update game state
        if game.state == 'PLAYING':
            keys = pygame.key.get_pressed()
            game.player.update(keys)

            # Update bullets
            for bullet in game.bullets[:]:
                bullet.update()
                if not bullet.active:
                    game.bullets.remove(bullet)

            for bullet in game.enemy_bullets[:]:
                bullet.update()
                if not bullet.active:
                    game.enemy_bullets.remove(bullet)

            # Update enemies
            move_down = False
            for enemy in game.enemies:
                enemy.update()
                # Check if hit edge
                if enemy.rect.left <= 0 or enemy.rect.right >= SCREEN_WIDTH:
                    move_down = True
                # Enemy shooting
                if enemy.should_shoot():
                    game.enemy_bullets.append(enemy.shoot())

            if move_down:
                for enemy in game.enemies:
                    enemy.drop_down()

            # Update power-ups
            for power in game.power_ups[:]:
                power.update()
                if not power.active:
                    game.power_ups.remove(power)

            # Check collisions
            game.handle_collisions()

            # Check level complete
            if not game.enemies:
                game.next_level()

        # Draw everything
        screen.fill(BLACK)

        if game.state == 'MENU':
            title = font_large.render('SPACE DEFENDER', True, GREEN)
            start = font_medium.render('Press SPACE to Start', True, WHITE)
            high = font_small.render(f'High Score: {game.high_score}', True, YELLOW)
            screen.blit(title, (SCREEN_WIDTH//2 - 250, 200))
            screen.blit(start, (SCREEN_WIDTH//2 - 150, 350))
            screen.blit(high, (SCREEN_WIDTH//2 - 80, 450))

        elif game.state == 'PLAYING':
            # Draw game objects
            game.player.draw(screen)
            for bullet in game.bullets:
                bullet.draw(screen)
            for bullet in game.enemy_bullets:
                bullet.draw(screen)
            for enemy in game.enemies:
                enemy.draw(screen)
            for power in game.power_ups:
                power.draw(screen)

            # Draw HUD
            score_text = font_small.render(f'Score: {game.score}', True, WHITE)
            level_text = font_small.render(f'Level: {game.level}', True, WHITE)
            lives_text = font_small.render(f'Lives: {game.player.lives}', True, GREEN)
            screen.blit(score_text, (10, 10))
            screen.blit(level_text, (10, 40))
            screen.blit(lives_text, (10, 70))

        elif game.state == 'PAUSED':
            pause_text = font_large.render('PAUSED', True, YELLOW)
            screen.blit(pause_text, (SCREEN_WIDTH//2 - 120, SCREEN_HEIGHT//2))

        elif game.state == 'GAME_OVER':
            over_text = font_large.render('GAME OVER', True, RED)
            score_text = font_medium.render(f'Final Score: {game.score}', True, WHITE)
            restart = font_small.render('Press R to Return to Menu', True, WHITE)
            screen.blit(over_text, (SCREEN_WIDTH//2 - 180, 200))
            screen.blit(score_text, (SCREEN_WIDTH//2 - 120, 300))
            screen.blit(restart, (SCREEN_WIDTH//2 - 140, 400))

        pygame.display.flip()
        clock.tick(FPS)

    pygame.quit()

if __name__ == '__main__':
    main()
