elifStatement.codeExample:
score = 850

if score >= 1000:
    print('PERFECT! S-Rank!')
elif score >= 800:
    print('Great job! A-Rank!')
elif score >= 600:
    print('Good! B-Rank!')
elif score >= 400:
    print('Not bad! C-Rank!')
else:
    print('Try again! D-Rank')

separateIfs.codeExample:
# Multiple power-ups can be active!
has_speed_boost = True
has_shield = True
has_double_points = False

if has_speed_boost:
    print('Speed x2!')  # Yes!

if has_shield:
    print('Protected!')  # Also yes!

if has_double_points:
    print('Double points!')  # Nope

tryLogic.codeExample:
enemy_hp = 50

# Enemy has 50, so > 100 is always false!
if enemy_hp > 100:
    print('Boss battle!')  # This never runs!

# Try this instead:
if enemy_hp > 75:
    print('Strong enemy!')
elif enemy_hp > 40:
    print('Regular enemy!')  # This would run!
else:
    print('Weak enemy!')

moreExamples.codeExample:
menu_choice = 2

if menu_choice == 1:
    print('Starting new game...')
    print('Welcome to the adventure!')
elif menu_choice == 2:
    print('Loading saved game...')
    print('Welcome back, hero!')
elif menu_choice == 3:
    print('Opening settings...')
else:
    print('Invalid choice! Try again.')

complexConditions.codeExample:
player_hp = 45
enemy_hp = 80
mana = 20
has_sword = True
has_magic = True

if enemy_hp > 50 and player_hp < 30:
    if mana >= 30 and has_magic:
        print('Cast healing spell!')
    elif has_sword:
        print('Defensive stance!')
    else:
        print('Retreat!')
elif enemy_hp <= 50 and player_hp > 30:
    print('All-out attack!')
else:
    print('Balanced approach!')

nestedIfs.codeExample:
inventory_slots = 10
items_count = 8
is_premium = True

if items_count < inventory_slots:
    print('Space available!')
    if is_premium:
        print('Premium bonus: +2 extra slots!')
        inventory_slots += 2
else:
    print('Inventory full!')
    if is_premium:
        print('Premium feature: Auto-organize!')

finalChallenge.codeExample:
has_key = False
has_lockpicks = True
player_level = 12
import random
luck = random.randint(1, 100)

# Your code here!
if has_key or has_lockpicks:
    print('Opening chest...')
    if player_level > 10:
        print('Rare item found!')
    elif luck > 50:
        print('Common item found!')
