functionCalls.codeExample:
def roll_dice():
    import random
    return random.randint(1, 6)

def attack_with_critical():
    base_damage = 10
    dice_roll = roll_dice()  # Call another function!

    if dice_roll == 6:
        print('CRITICAL HIT!')
        return base_damage * 3
    else:
        return base_damage

# Use it
damage = attack_with_critical()
print(f'Dealt {damage} damage!')

practiceChallenge.codeExample:
# Shop System Challenge
player_gold = 100
inventory = []

def show_shop(items):
    print('=== SHOP ===')
    for i, item in enumerate(items, 1):
        print(f"{i}. {item['name']} - {item['price']}g")

def buy_item(item, gold):
    if gold >= item['price']:
        inventory.append(item['name'])
        return gold - item['price']
    else:
        print('Not enough gold!')
        return gold

def sell_item(item_name, sell_price):
    global player_gold
    if item_name in inventory:
        inventory.remove(item_name)
        player_gold += sell_price
        print(f'Sold {item_name} for {sell_price}g!')
    else:
        print("You don't have that item!")

# Test the shop!
shop_items = [
    {'name': 'Sword', 'price': 50},
    {'name': 'Potion', 'price': 20}
]

show_shop(shop_items)
player_gold = buy_item(shop_items[1], player_gold)
print(f'Gold: {player_gold}, Inventory: {inventory}')
