accessObject.codeExample:
let player = {
    name: 'Shadow Knight',
    level: 5,
    health: 150,
    skills: ['slash', 'block', 'charge']
};

// Access with dot notation (most common)
let name = player.name;  // 'Shadow Knight'

// Access with bracket notation (useful for variables!)
let statName = 'level';
let level = player[statName];  // 5

// Access nested data
let firstSkill = player.skills[0];  // 'slash'

modifyObject.codeExample:
let player = { name: 'Hero', level: 1, exp: 0 };

// Change existing value
player.level = 2;  // Level up!

// Add new key-value pair
player.class = 'Warrior';  // New attribute!
player.gold = 100;

// Add multiple values at once
Object.assign(player, {
    health: 150,
    mana: 75
});

console.log(player);  // See all the changes!

removeKeys.codeExample:
let inventory = {
    potion: 3,
    bomb: 5,
    key: 1
};

// Remove with 'delete' keyword
delete inventory.key;  // Used the key!

console.log(inventory); // { potion: 3, bomb: 5 }

objectMethods.codeExample:
let player = { name: 'Hero', level: 10, gold: 500 };

// Get all keys
let keys = Object.keys(player);
// ['name', 'level', 'gold']

// Get all values
let values = Object.values(player);
// ['Hero', 10, 500]

// Check if key exists
if ('level' in player) {
    console.log(`Level: ${player.level}`);
}

// Loop through object entries
for (let [key, value] of Object.entries(player)) {
    console.log(`${key}: ${value}`);
}

