practiceChallenge.codeExample:
// Advanced Inventory System
let inventory = {
    weapons: { sword: 1, bow: 1 },
    consumables: { potion: 5, food: 10 }
};

function addItem(category, item, qty = 1) {
    if (inventory[category]) {
        if (inventory[category][item]) {
            inventory[category][item] += qty;
        } else {
            inventory[category][item] = qty;
        }
        console.log(`Added ${qty} ${item}(s)`);
    }
}

function useItem(category, item) {
    if (inventory[category] && inventory[category][item] > 0) {
        inventory[category][item] -= 1;
        console.log(`Used ${item}`);
        if (inventory[category][item] === 0) {
            delete inventory[category][item];
        }
        return true;
    }
    console.log(`No ${item} available!`);
    return false;
}

addItem('weapons', 'dagger');
useItem('consumables', 'potion');
console.log(inventory);
