# Advanced Inventory System
inventory = {
    'weapons': {'sword': 1, 'bow': 1},
    'armor': {'helmet': 1, 'chestplate': 1},
    'consumables': {'potion': 5, 'food': 10}
}

def add_item(category, item, quantity=1):
    if category in inventory:
        if item in inventory[category]:
            inventory[category][item] += quantity
        else:
            inventory[category][item] = quantity
        print(f'Added {quantity} {item}(s)')

def use_item(category, item):
    if category in inventory and item in inventory[category]:
        if inventory[category][item] > 0:
            inventory[category][item] -= 1
            print(f'Used {item}')
            if inventory[category][item] == 0:
                del inventory[category][item]
            return True
    print(f'No {item} available!')
    return False

def show_inventory():
    print('=== INVENTORY ===')
    for category, items in inventory.items():
        print(f'{category.upper()}:')
        for item, qty in items.items():
            print(f'  {item}: {qty}')

# Test it!
add_item('weapons', 'sword', 2)
use_item('consumables', 'potion')
show_inventory()
